define({"topics" : [{"title":"Configuring SDC Edge","shortdesc":"\n               <p class=\"shortdesc\">You can customize <span class=\"ph\">SDC Edge</span> by         editing the <span class=\"ph\">SDC Edge</span>         configuration file, <code class=\"ph codeph\">&lt;SDCEdge_home&gt;\/etc\/edge.conf</code>.\n               </p>\n            ","id":"concept_hbv_3wl_pbb","href":"datacollector\/UserGuide\/Edge_Mode\/SDCeAdminister.html#concept_hbv_3wl_pbb","menu": {"hasChildren":false,},"tocID":"concept_hbv_3wl_pbb-d46e107519","topics":[]},{"title":"Starting SDC Edge","id":"concept_zfb_lwl_pbb","href":"datacollector\/UserGuide\/Edge_Mode\/SDCeAdminister.html#concept_zfb_lwl_pbb","menu": {"hasChildren":false,},"tocID":"concept_zfb_lwl_pbb-d46e107553","topics":[]},{"title":"Shutting Down SDC Edge","id":"concept_tyz_mwl_pbb","href":"datacollector\/UserGuide\/Edge_Mode\/SDCeAdminister.html#concept_tyz_mwl_pbb","menu": {"hasChildren":false,},"tocID":"concept_tyz_mwl_pbb-d46e107585","topics":[]},{"title":"Logs","shortdesc":"\n               <p class=\"shortdesc\"><span class=\"ph\">By default, <span class=\"ph\">SDC Edge</span> writes log messages at                 the info severity level to the <code class=\"ph codeph\">&lt;SDCEdge_home&gt;\/log\/edge.log</code> file.                 To view the logs, simply open the <code class=\"ph codeph\">edge.log</code> file in a text                 editor.</span></p>\n            ","id":"concept_ecy_hjx_scb","href":"datacollector\/UserGuide\/Edge_Mode\/SDCeAdminister.html#concept_ecy_hjx_scb","menu": {"hasChildren":false,},"tocID":"concept_ecy_hjx_scb-d46e107627","topics":[]}]});