define({"topics" : [{"title":"Field Mappings","shortdesc":"\n               <p class=\"shortdesc\">When you configure the MapR DB destination, you map fields from records to MapR DB         columns.</p>\n            ","id":"concept_tr1_143_1w","href":"datacollector\/UserGuide\/Destinations\/MapRDB.html#concept_tr1_143_1w","menu": {"hasChildren":false,},"tocID":"concept_tr1_143_1w-d46e85432","topics":[]},{"title":"Time Basis","shortdesc":"\n               <p class=\"shortdesc\">The time basis determines the timestamp value added for each column written to MapR DB. </p>\n            ","id":"concept_qxm_hq3_1w","href":"datacollector\/UserGuide\/Destinations\/MapRDB.html#concept_qxm_hq3_1w","menu": {"hasChildren":false,},"tocID":"concept_qxm_hq3_1w-d46e85457","topics":[]},{"title":"Kerberos Authentication","shortdesc":"\n               <p class=\"shortdesc\">You can use Kerberos authentication to connect to MapR DB. When you use Kerberos         authentication, <span class=\"ph\">Data Collector</span> uses         the Kerberos principal and keytab to connect to MapR DB. By default, <span class=\"ph\">Data Collector</span> uses         the user account who started it to connect.\n               </p>\n            ","id":"concept_fvq_r43_1w","href":"datacollector\/UserGuide\/Destinations\/MapRDB.html#concept_fvq_r43_1w","menu": {"hasChildren":false,},"tocID":"concept_fvq_r43_1w-d46e85492","topics":[]},{"title":"Using an HBase User","id":"concept_hv2_4p3_1w","href":"datacollector\/UserGuide\/Destinations\/MapRDB.html#concept_hv2_4p3_1w","menu": {"hasChildren":false,},"tocID":"concept_hv2_4p3_1w-d46e85543","topics":[]},{"title":"HDFS Properties and Configuration File","id":"concept_fth_vq3_1w","href":"datacollector\/UserGuide\/Destinations\/MapRDB.html#concept_fth_vq3_1w","menu": {"hasChildren":false,},"tocID":"concept_fth_vq3_1w-d46e85595","topics":[]},{"title":"Configuring a MapR DB Destination","id":"task_pgk_p2z_yv","href":"datacollector\/UserGuide\/Destinations\/MapRDB.html#task_pgk_p2z_yv","menu": {"hasChildren":false,},"tocID":"task_pgk_p2z_yv-d46e85657","topics":[]}]});