define({"topics" : [{"title":"Field Mappings","shortdesc":"\n               <p class=\"shortdesc\">When you configure the HBase destination, you map fields from records to HBase         columns.</p>\n            ","id":"concept_vn5_cr5_4v","href":"datacollector\/UserGuide\/Destinations\/HBase.html#concept_vn5_cr5_4v","menu": {"hasChildren":false,},"tocID":"concept_vn5_cr5_4v-d46e80728","topics":[]},{"title":"Kerberos Authentication","shortdesc":"\n               <p class=\"shortdesc\">You can use Kerberos authentication to connect to HBase. When you use Kerberos   authentication, <span class=\"ph\">Data Collector</span> uses the   Kerberos principal and keytab to connect to HBase. By default, <span class=\"ph\">Data Collector</span> uses the user   account who started it to connect.\n               </p>\n            ","id":"concept_xy5_4tm_vs","href":"datacollector\/UserGuide\/Destinations\/HBase.html#concept_xy5_4tm_vs","menu": {"hasChildren":false,},"tocID":"concept_xy5_4tm_vs-d46e80753","topics":[]},{"title":"Using an HBase User","id":"concept_gbt_fpt_ls","href":"datacollector\/UserGuide\/Destinations\/HBase.html#concept_gbt_fpt_ls","menu": {"hasChildren":false,},"tocID":"concept_gbt_fpt_ls-d46e80794","topics":[]},{"title":"Time Basis","shortdesc":"\n               <p class=\"shortdesc\">The time basis determines the timestamp value added for each column written to HBase. </p>\n            ","id":"concept_t4g_vc2_wv","href":"datacollector\/UserGuide\/Destinations\/HBase.html#concept_t4g_vc2_wv","menu": {"hasChildren":false,},"tocID":"concept_t4g_vc2_wv-d46e80836","topics":[]},{"title":"HDFS Properties and Configuration File","id":"concept_tjp_v5l_zq","href":"datacollector\/UserGuide\/Destinations\/HBase.html#concept_tjp_v5l_zq","menu": {"hasChildren":false,},"tocID":"concept_tjp_v5l_zq-d46e80891","topics":[]},{"title":"Configuring an HBase Destination","id":"task_pyq_qx5_vr","href":"datacollector\/UserGuide\/Destinations\/HBase.html#task_pyq_qx5_vr","menu": {"hasChildren":false,},"tocID":"task_pyq_qx5_vr-d46e80953","topics":[]}]});